using System.Collections;using System.Collections.Generic;using UnityEngine;public class Inventory : MonoBehaviour{    // List of the objects in the inventory    private List<GameObject> listOfObjects;    // Size of the inventory    public int inventorySize = 5;    private void Awake()    {        listOfObjects = new List<GameObject>();    }    /**     * Adds an object to the objects in the inventory.     *      * @param pickedUpObject    Object picked to be added to the inventory.     * @return true if the object can be added, false otherwise     */    public bool AddObject(GameObject pickedUpObject)    {        if (listOfObjects.Count < inventorySize)        {            listOfObjects.Add(pickedUpObject);            return true;        }        return false;    }}